mapper.insert:

   SELECT notice_seq.nextval
   FROM DUAL

INSERT INTO notice
(
   notice_idx
   , userinfo_id
   , notice_title
   , notice_content
   , notice_img
   , notice_regdate
   , notice_status
) VALUES (
   #{noticeIdx}
   , #{userinfoId}
   , #{noticeTitle}
   , #{noticeContent}
   , #{noticeImg}
   , sysdate
   , #{noticeStatus}
)

mapper.update:
UPDATE notice SET notice_title = #{noticeTitle}, notice_content = #{noticeContent}, notice_regdate = sysdate, notice_img =
#{noticeImg}
WHERE notice_idx = #{noticeIdx}

UPDATE notice
SET notice_viewcnt = notice_viewcnt + 1
WHERE notice_idx = #{noticeIdx}
