mapper.insert:

    SELECT orders_seq.nextval 
    FROM dual

INSERT INTO orders (
        orders_idx
        , userinfo_id
        , pack_idx
        , orders_date
        , pack_previewimg
        , pack_title
) VALUES (
        #{ordersIdx}
        , #{userinfoId}
        , #{packIdx}
        , sysdate
        , #{packPreviewImg}
        , #{packTitle}
)
