mapper.insert:

	SELECT pack_seq.nextval 
	FROM DUAL

INSERT INTO 
pack (
	pack_idx
	, pack_title
	, pack_kind
	, pack_preview
	, pack_main
	, pack_content
	, pack_calendar
	, pack_startdate
	, pack_enddate
	, pack_regdate
	, pack_people
	, pack_childcount
	, pack_adultcount
	, pack_adultprice
	, pack_childprice
	, pack_status
) 
VALUES (
	#{packIdx}
	, #{packTitle}
	, #{packKind}
	, #{packPreview}
	, #{packMain}
	, #{packContent}
	, #{packCalendar}
	, #{packStartDate}
	, #{packEndDate}
	, sysdate
	, #{packPeople}
	, #{packChildCount}
	, #{packAdultCount}
	, #{packAdultPrice}
	, #{packChildPrice}
	, #{packStatus}
)
