mapper.insert:

	SELECT payment_seq.nextval 
	FROM dual

INSERT INTO payment( 
		imp_uid
		, merchant_uid
		, pack_idx
		, userinfo_id
		, payment_amount	
		, payment_date
		, payment_status
		, payment_idx
)VALUES(
		#{impUid}
		, #{merchantUid}
		, #{packIdx}
		, #{userinfoId}
		, #{paymentAmount}
		, sysdate
		, 0
		, #{paymentIdx}
)

mapper.update:
UPDATE payment 
SET
	payment_status = 0
	, payment_date = sysdate
	, IMP_UID = #{impUid}
	, MERCHANT_UID = #{merchantUid}
WHERE payment_idx = #{paymentIdx}

UPDATE payment 
SET
	payment_status = 1
	, payment_date = sysdate
	, IMP_UID = #{impUid}
	, MERCHANT_UID = #{merchantUid}
WHERE payment_idx = #{paymentIdx}

UPDATE payment
SET payment_status = #{paymentStatus}
WHERE imp_uid=#{impUid} AND userinfo_id=#{userinfoId}
