mapper.insert:

SELECT qa_seq.nextval
FROM DUAL

INSERT INTO 
qa (
	qa_idx
	, userinfo_id
	, qa_option
	, qa_title
	, qa_content
	, qa_img1
	, qa_img2
	, qa_img3
	, qa_regdate
)

VALUES (
	#{qaIdx}
	, #{userinfoId}
	, #{qaOption}
	, #{qaTitle}
	, #{qaContent}
	, #{qaImg1}
	, #{qaImg2}
	, #{qaImg3}
	, sysdate
)

mapper.update:
UPDATE qa
SET qa_reply_status = 1
WHERE qa_idx = #{qaIdx}

UPDATE qa
SET qa_reply_status = 0
WHERE qa_idx = #{qaIdx}
