mapper.insert:

    SELECT review_seq.nextval FROM dual

INSERT INTO review 
VALUES(
    #{reviewIdx}
    , #{idReview}
    , #{ordersIdx} 
    , #{reviewStar}
    , #{reviewTitle}
    , #{reviewContent}
    , #{reviewRegdate}
)

mapper.update:
UPDATE review SET 
	review_title=#{reviewTitle}
	, review_content=#{reviewContent} 
	WHERE review_title=#{reviewTitle}
