mapper.insert:

    SELECT review_seq.nextval FROM dual 

INSERT INTO review 
(
    review_idx,
    userinfo_id,
    orders_idx,
    review_star,
    pack_title,
    review_title,
    review_img,
    review_content,
    review_regdate
)
VALUES(
    #{reviewIdx}
    , #{userinfoId}
    , #{ordersIdx} 
    , #{reviewStar}
    , #{packTitle}
    , #{reviewTitle}
    , #{reviewImg}
    , #{reviewContent}
    , sysdate
)

mapper.update:
UPDATE review SET 
    review_star=#{reviewStar} 
    , review_title=#{reviewTitle}
    , review_img=#{reviewImg}
    , review_content=#{reviewContent} 
    WHERE review_idx=#{reviewIdx}

UPDATE review
SET review_viewcnt = review_viewcnt + 1
WHERE review_idx = #{reviewIdx}

mapper.select:
select * from (select rownum rn, review.* from (select review_idx , userinfo_id , orders_idx , review_star , pack_title , review_title , review_img , review_content , review_regdate , review_viewcnt from review
where ${column} like #{word}
order by review_idx desc) review) where rn between #{startRow} and #{endRow}
